CREATE TABLE stock_market(
   Date            VARCHAR(10) NOT NULL PRIMARY KEY
  ,Company         VARCHAR(17) NOT NULL
  ,Sector          VARCHAR(14) NOT NULL
  ,Open            VARCHAR(11) NOT NULL
  ,High            VARCHAR(11) NOT NULL
  ,Low             VARCHAR(11) NOT NULL
  ,Close           VARCHAR(11) NOT NULL
  ,Volume          VARCHAR(6) NOT NULL
  ,Market_Cap      VARCHAR(11) NOT NULL
  ,PE_Ratio        VARCHAR(11) NOT NULL
  ,Dividend_Yield  VARCHAR(11) NOT NULL
  ,Volatility      VARCHAR(11) NOT NULL
  ,Sentiment_Score VARCHAR(12) NOT NULL
  ,Trend           VARCHAR(7) NOT NULL
);
